V = ARGV.delete("-v")

TEST = <<TEST
     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+ 
TEST

GRID = []

INPUT = (ARGV.delete("-t") ? TEST : DATA).each_line.map do |line|
  GRID << line.each_char.reject { |c| c == "\n" }
end

pos = [GRID.first.index { |c| c == "|" }, 0]
dir = ?D
seen = []
@traveled = 0

def travel(curpos, dir)
  case dir
  when ?D
    curpos[1] += 1
  when ?U
    curpos[1] -= 1
  when ?L
    curpos[0] -= 1
  when ?R
    curpos[0] += 1
  else
    raise "unknown #{curpos} #{dir}"
  end
  @traveled += 1
  curpos
end

def find_next_dir_at_turn(curpos, dir)
  case dir
  when ?U, ?D
    if curpos[0] < GRID.first.length - 1
      if GRID[curpos[1]][curpos[0] + 1] != " "
        ?R
      else
        ?L
      end
    else
      ?L
    end
  when ?L, ?R
    if curpos[1] < GRID.length - 1
      if GRID[curpos[1] + 1][curpos[0]] != " "
        ?D
      else
        ?U
      end
    else
      ?U
    end
  end
end

loop do
  current = GRID[pos[1]][pos[0]]
  if pos[0] * pos[1] < 0 || current == " " # when reaching the end, we see a letter and continue, if space, then it breaks
    puts seen.join
    puts @traveled
    break
  end
  if current == ?+
    dir = find_next_dir_at_turn(pos, dir)
  end
  pos = travel(pos, dir)
  if ("A".."Z").include?(current)
    seen << current
  end
  pp [dir, pos] if V
end

__END__
